# Create a contract object
uniswap_pool_contract = web3.eth.contract(address=uniswap_pool_address, abi=uniswap_pool_abi)

# Define the event signature for the Swap event
event_signature = web3.keccak(text="Swap(address,address,int256,int256,uint160,uint128,int24)").hex()


# Assuming token0 is the base token and token1 is the quote token
# Token0 and Token1 decimals 
token0_decimals = 6 # Replace with actual decimals for token0
token1_decimals = 18 # Replace with actual decimals for token1


# Define a function to get historical Swap events
def get_historical_swaps(start, end_block):
    logs = web3.eth.get_logs({
        "fromBlock": start,
        "toBlock": end_block,
        "address": uniswap_pool_address,
        "topics": [event_signature]
    })
    return logs

# Assuming token0 is the base token and token1 is the quote token
# Token0 and Token1 decimals 
token0_decimals = 6 # Replace with actual decimals for token0
token1_decimals = 18 # Replace with actual decimals for token1

def fetch_batch_data(start_block, end_block):

    logs = get_historical_swaps(start_block, end_block)
    historical_data = []
    for log in logs:
        try:
            decoded_log = uniswap_pool_contract.events.Swap().process_log(log)
            sqrt_price_x96 = decoded_log['args']['sqrtPriceX96']
            tick = decoded_log['args']['tick']
            liquidity = decoded_log['args']['liquidity']/(10**((token0_decimals+token1_decimals)/2))
            sender = decoded_log['args']['sender']
            recipient = decoded_log['args']['recipient']
            amount0 = decoded_log['args']['amount0']/(10**token0_decimals)
            amount1 = decoded_log['args']['amount1']/(10**token1_decimals)
            block_number = log['blockNumber']
            transaction_hash = log['transactionHash'].hex()
            timestamp = web3.eth.get_block(log['blockNumber'])['timestamp']
            normalized_sqrt_price = sqrt_price_x96 / (2 ** 96)
            price = (1/(normalized_sqrt_price ** 2)) * (10 ** (token1_decimals - token0_decimals))# Adjust for token decimals

            historical_data.append({
                "block_number": block_number,
                "timestamp": datetime.fromtimestamp(timestamp, tz=timezone.utc).strftime('%Y-%m-%d %H:%M:%S'),
                "price ETH/USDC": price,
                "sqrtPriceX96": sqrt_price_x96,
                "tick": tick,
                "liquidity": liquidity,
                "sender": sender,
                "recipient": recipient,
                "amount0": amount0,
                "amount1": amount1,
                "transaction_hash": transaction_hash
            })
        except Exception as e:
            print(f"Error processing log: {e}")

    return historical_data

def process_logs_to_dataframe(logs):
    df = pd.DataFrame(logs)
    return df

def save_dataframe_to_csv(df, file_name):
    df.to_csv(file_name, mode='a', index=False, header=not pd.io.common.file_exists(file_name))

def main():
    start_block = 15614000 # Starting block number
    end_block = 20217705  # Approximate block number for 2 years
    batch_size = 1000 # Number of blocks per batch
    output_file = './../historical_swaps.csv'

    # Fetch, process, and save logs in batches
    for block_num in range(start_block, end_block, batch_size):
        print(f"Fetching blocks from {block_num} to {min(block_num + batch_size, end_block) - 1}")
        logs_batch = fetch_batch_data(block_num, min(block_num + batch_size, end_block)-1)
        
        if logs_batch:
            df = process_logs_to_dataframe(logs_batch)
            save_dataframe_to_csv(df, output_file)
        else:
            print(f"No logs found in blocks {block_num} to {min(block_num + batch_size, end_block) - 1}")

if __name__ == "__main__":
    main()